import { useState } from "react";
import { useHistory, Link } from "react-router-dom";
import { useToasts } from "react-toast-notifications";

import styles from "../styles/login.module.css";

const CreateUser = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [status, setStatus] = useState("");
  const [submittingUp, setSubmittingUp] = useState("");
  const { addToast } = useToasts();
  const history = useHistory();

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    setSubmittingUp(true);

    let error = false;
    if (!email || !email || !phone || !status) {
      addToast("Please fill all the fields", {
        appearance: "error",
        autoDismiss: true,
      });
      error = true;
    }

    if (error) {
      return setSubmittingUp(false);
    }

    const response = await fetch("http://localhost:8000/users/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        email: email,
        password: password,
      }),
    });

    // console.log(response);
    const res = await response.json();
    console.log(res);

    if (res.success) {
      history.push("/login");
      addToast("User registered successfully, please login now", {
        appearance: "success",
        autoDismiss: true,
      });
    } else {
      addToast(res.message, {
        appearance: "error",
        autoDismiss: true,
      });
    }

    setSigningUp(false);
  };

  return (
    <form className={styles.loginForm} onSubmit={handleFormSubmit}>
      <span className={styles.loginSignupHeader}> Add A User</span>
      <div className={styles.field}>
        <input
          placeholder="Name"
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div className={styles.field}>
        <input
          placeholder="Email"
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>
      <div className={styles.field}>
        <input
          placeholder="Phone"
          type="number"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
      </div>
      <div className={styles.field}>
        <input
          placeholder="Status"
          type="text"
          required
          value={status}
          onChange={(e) => setStatus(e.target.value)}
        />
      </div>
      <div className={styles.field}>
        <button disabled={submittingUp}>
          {submittingUp ? "Submitting up..." : "submit"}
        </button>
      </div>
    </form>
  );
};

export default CreateUser;
